mapper.select:
SELECT question_id as questionId, question_title as questionTitle, user_id as userId, question_datetime as questionDatetime FROM question_board
question_title like CONCAT('%',#{searchWord},'%')
ORDER BY question_datetime DESC LIMIT #{beginRow},#{rowPerPage}

mapper.insert:
INSERT INTO expert(
	user_id,
	feild_id,
	expert_license_no,
	expert_license_issue_date
)VALUE(
	#{userId}, #{feildId}, #{expertLicenseNo},#{expertLicenseIssueDate}
)

INSERT INTO user(
	user_id,
	user_name,
	user_pw,
	user_birthdate,
	user_createtime,
	region_id
) values (
	#{userId}, #{userName}, #{userPw},#{userBirthDate}, now(),#{regionId}
)
